mapper.select:



SELECT bno, title, content, writer, regdate, updatedate, hit
FROM(
	SELECT /*+INDEX_DESC(tbl_board) */ rownum  as rn, bno, title, content, writer, regdate, updatedate, hit
    FROM tbl_board
    WHERE rownum <= #{pageNum} * #{amount}
    ) 

WHERE rn > (#{pageNum} -1) * #{amount}

mapper.update:

UPDATE tbl_board 
SET hit = hit+1
WHERE BNO = #{bno}
